import {enemyBoxes} from "../dom.js"
import * as enemyClass from "../enemies/enemyClass.js"
import { player } from "../player.js"
import pubsub from "../pubsub.js"
import { basicDamageFormula } from "./damage_formulas.js"



// enemies
export function initEnemies() {

  const randomEnemyBoxes = getRandomEnemyBoxes(3)

  const enemy1 = new enemyClass.bat() 
  const enemy2 = new enemyClass.tiger() 
  const enemy3 = new enemyClass.bruv() 

  randomEnemyBoxes[0].append(enemy1.modal)
  randomEnemyBoxes[1].append(enemy2.modal)
  randomEnemyBoxes[2].append(enemy3.modal)
}

    export function getRandomEnemyBoxes(num) {
      const all_EnemyBoxes = Array.from(enemyBoxes)
      const random_Boxes = []
      for(let i = 1 ; i <= num ;i++){
        let random_index = Math.floor(Math.random() * (all_EnemyBoxes.length ) )
        random_Boxes.push(...all_EnemyBoxes.splice(random_index , 1))
      }
      return random_Boxes
    }
// /enemies


//damage
export function countDamage(recievingEntity , DealingEntity ,damageInfoObj) {
  if (damageInfoObj.counter) {
   return damageInfoObj.counter(recievingEntity , DealingEntity , damageInfoObj.damage)
  }
  return basicDamageFormula(recievingEntity , DealingEntity , damageInfoObj.damage)
}

export function iterateDealDamageEffects(DealingEntity ,damageObj ) {
  for(const effect in DealingEntity.dealDamageEffects ){
    DealingEntity.dealDamageEffects[effect].listEffect(damageObj)
  }
}

export function iterateRecieveDamageEffects(recievingEntity ,damageObj ) {

  for(const effect in recievingEntity.recieveDamageEffects){
    recievingEntity.recieveDamageEffects[effect].listEffect(damageObj)
  }
}

export function iterateGainBlockEffects(recievingEntity , block) {
  for(const effect in recievingEntity.gainBlockEffects ){
    recievingEntity.gainBlockEffects[effect].listEffect(block)
  }
}
export function iterateEnergyEffects(energyObj) {
  for(const effect in player.energyEffects){
    player.energyEffects[effect].listEffect(energyObj)
  }
}
// /damage

// check
export function checkEntityDeath(entity) {
  if(entity.health <= 0){
    entity.die(entity)
  }
}
// /check
