import {player} from "./player.js"
import {enemyBoxes,handElement,restOfPage,enemyGrid} from "./dom.js"
import * as dom from "./dom.js"

import * as battlefunctions from "./functions/battleFunctions.js"
import * as cardfunctions from "./functions/cardFunctions.js"
import * as domfunctions from "./functions/domFunctions.js"
import * as playerfunctions from "./functions/playerFuctions.js"
import * as damageFormulas from "./functions/damage_formulas.js"

export {battlefunctions ,cardfunctions ,domfunctions , playerfunctions , damageFormulas}
import {selectedCard} from "./functions/domFunctions.js"
import effects from "./buffsManager.js"
import pubsub from "./pubsub.js"

// init

(function(){
  pubsub.on("turnStarted" , {effect:defaultTurnStarted})
  pubsub.on("turnEnded" , {effect:defaultTurnEnded})


  dom.endTurnBtn.addEventListener("pointerdown" , emitTurnEnded)

  restOfPage.addEventListener("pointerdown" , (e)=> {
    selectionHandler()
    playSelectedCard(e)
  })
  enemyBoxes[0].__proto__.move = moveEnemyBox
})()
function emitTurnEnded() {
  pubsub.turnEnded()
}
// /init


          // Dom Functions

// render
export const render = (function(){
let previous_hand = []
return function(){
domfunctions.deSelectAllCards()

  let hand = player.hand ;
  let discarded_card_array = previous_hand.filter(x => !hand.includes(x))
      let drawn_card_array = hand.filter(x => !previous_hand.includes(x) )
  previous_hand = Array.from(player.hand)

  discarded_card_array.forEach(card => {
    domfunctions.visualyRemoveCard(card)
  })

  drawn_card_array.forEach(card => {
    domfunctions.visualyRenderCard(card)
  })



 }
})()


export function renderHealth(entity) {
const maxHealth = entity.maxHealth
const health = entity.health

const healthInfo = domfunctions.getEntityHealthModals(entity)

  const health_percent = ( health / maxHealth )

  domfunctions.renderHealthBar(entity)

  domfunctions.renderHealthCount(entity)

  domfunctions.renderBlock(entity)

  domfunctions.renderHealthD(healthInfo.healthDisplay ,health_percent )
    setTimeout( ()=>{
  domfunctions.renderHealthD(healthInfo.healthDrag ,health_percent)
  }, 700);

  battlefunctions.checkEntityDeath(entity)
}
export function renderEffect(entity , effect) {
  domfunctions.generateEffectEllement(entity , effect)

  const effectEllemt = entity.effectGrid.querySelector(`.${effect.name}`)
    const usesElement = effectEllemt.querySelector(".effectUses")
    const duratioonEllement = effectEllemt.querySelector(".effectDuration")

    if (effect.uses <=0 || effect.duration <= 0) {
      effectEllemt.remove()
      return
    }

    usesElement.innerText =(effect.uses == "inf")? " ": effect.uses
    duratioonEllement.innerText = (effect.duration == "inf")? "":effect.duration
  //check if element exists
      // create if not

  // update uses and duration
}

function renderActionIcons() {
  const gridEntities = enemyGrid.querySelectorAll(".entity")
    gridEntities.forEach(entityModal => {
    const entity = entityModal.entity ,
          move = entity.moves[entity.turn]
          move.render()
      // entity.actionIcon.innerText = 0
      // entity.actionIcon.innerText = move?.damage ?? ""
    })
}
// /render

export function selectionHandler() { 
if(!selectedCard){
  domfunctions.deSelectAllEntities()
  return}
  let selected_card = selectedCard.cardObject

  if(selected_card.target === "enemy"){
    // console.log("play on enemy");
          // de select friends if we switch cards
          domfunctions.deSelectAllEntities(false)
    domfunctions.selectAllEnemies() 
  }
  else if(selected_card.target === "friendly"){
    // console.log("play on friendly");
          // de select all enemies if we switch cards
          domfunctions.deSelectAllEntities(false)
    domfunctions.selectAllFriendlies() 
  }
  else if(selected_card.target === "player"){
    // console.log("play on player");
          // de select all enemies if we switch cards
          domfunctions.deSelectAllEntities(false)
    domfunctions.selectPlayer() 
  }
//fall back
  else{
    alert("selected card" , selectedCard , "no have target")}
}

export function killEntityModal(entity) {
if (entity.modal == undefined) {return}
  const entity_selector = entity.modal.querySelector(".selector")
  entity_selector.remove()
  entity.modal.classList.add("dying")
  setTimeout(() => {
    entity.modal.remove()
  }, entity.deathDelay);
}

// moveBoxes

function moveEnemyBox(box) {
  const boxEnemyModal = this.querySelector(".enemy")
  // console.log(domfunctions.findEnemyBoxPosition(this) , this , boxEnemyModal);

if(boxEnemyModal == null) {   domfunctions.moveNextEnemyBoxOf(this) ; return  }
  const boxEnemyEntity = boxEnemyModal.entity

  boxEnemyEntity.move()

setTimeout(() => {
  domfunctions.moveNextEnemyBoxOf(this)

},/*wait*/ boxEnemyEntity.delay);



}
// /moveBoxes

// animations

export function animationHandler(entity , damageObj) {
  domfunctions.renderEntityAttackedAnimation(entity , damageObj )
}

// /animations


        // /Dom Functions




        // Battle Fucntion


// play cardon enemy
// turns
export function defaultTurnStarted() {
  clearBlockOfType("friendly")
  resetPlayerEnergy()
  drawCardsIntoHand()
  renderActionIcons()
  enableEndTurnBtn()
}
export function defaultTurnEnded(params) {
  disableEndTurnBtn()
  clearBlockOfType("enemy")
  endTurnClearPlayerHand()
  enemyBoxes[0].move()

}
  function disableEndTurnBtn() {
    dom.endTurnBtn.removeEventListener("pointerdown" , emitTurnEnded)
  }
  function enableEndTurnBtn() {
    dom.endTurnBtn.addEventListener("pointerdown" , emitTurnEnded)
  }

export function clearBlockOfType(entityType) {
  const target_entities = document.querySelectorAll(`.entity.${entityType}`)
  target_entities.forEach(entityModal => {
    const entity = entityModal.entity
    clearBlock(entity)
  })

}

export function resetPlayerEnergy() {
  const energy = {val : player.maxEnergy}
  battlefunctions.iterateEnergyEffects(energy)

  player.energy = energy.val
  domfunctions.renderPlayerEnergy()
}

// /turns

export function playSelectedCard(e) {
  const is_selector = domfunctions.checkIfIncludesClass(e.target , "selector")
if(!is_selector){return}
  const selector = e.target
  const card = selectedCard.cardObject
  const entity = selector.parentNode.entity
  playCardManager(card , entity)
}
export function playCardManager(card , entity){
  const energyCost = card.energyCost
  const energy = player.energy
      if (energyCost > energy) {
        domfunctions.deSelectAllCards()
        return
      }
  player.energy -= energyCost
  card.play(entity)
  domfunctions.renderPlayerEnergy()
}

// {
//   damage: 34,
//   damageCounter : ()=> {}
//   animation : dom Element
// }

export function damage(recievingEntity , DealingEntity ,damageInfoObj) {

  let countedDamage = battlefunctions.countDamage(recievingEntity , DealingEntity ,damageInfoObj) 
  let damageObj ={ val : countedDamage,
                   animation : damageInfoObj.animation ,
                   status: true}

// loop through dealDamage Effects
battlefunctions.iterateDealDamageEffects(DealingEntity ,damageObj )

// loop through recieveDamageEffects Effects
battlefunctions.iterateRecieveDamageEffects(recievingEntity ,damageObj ) 

if (damageObj.status === false) { return }

// console.log(`attack function : ${recievingEntity.name} recieved ${damageObj.val}`
// , recievingEntity);
  const damageFinalValue = (damageObj.val < 0)? 0 : damageObj.val
  let unblockedDamage = damageBlock(damageFinalValue , recievingEntity)
  recievingEntity.health -= unblockedDamage


  animationHandler(recievingEntity  , damageObj )
  renderHealth(recievingEntity)
}



export function directlyDamage( damage , entity) {
  entity.health -= damage
  renderHealth(entity)
}

export function damageBlock(damage , entity){
  const block = entity.block
    if(block >= damage){
      entity.block -= damage
      return 0
    }

    else{
      entity.block = 0
      return (damage - block)
    }
}


// /play cardon enemy




// enemy functions
export function attackFriendly(DealingEntity , damageInfoObj) {
  const nextFriendly = domfunctions.findNextFriendly(DealingEntity)
  damage(nextFriendly , DealingEntity , damageInfoObj)
}

export function gainBlock(  recievingEntity , initialBlock ) {
  let block = {val : initialBlock ,
               status : true}

  battlefunctions.iterateGainBlockEffects(recievingEntity , block)


  if(block.status === false){return}

  recievingEntity.block += block.val
  renderHealth(recievingEntity)
}

// / enemy functions

export function clearBlock(entity){
const blockClearer = {
  initialBlock : entity.block , 
  clearedBlock : entity.block ,
  status : true
}

for(const effect in entity.clearBlockEffects){
  entity.clearBlockEffects[effect].listEffect(blockClearer)
}

if(blockClearer.status === false){return}
  const finalClearedBlock = (blockClearer.clearedBlock < 0)? 0 :  blockClearer.clearedBlock
  entity.block = blockClearer.initialBlock - finalClearedBlock
    renderHealth(entity)
}

export function resetEntity(entity){
entity.apply = ()=> {}
  for(const effect in entity.buffs){
    entity["buffs"][effect]._forceRemove()
  }
  for(const effect in entity.debuffs){
    entity["debuffs"][effect]._forceRemove()
  }

}


        // /Battle Fucntion






        // Card Functions


export function drawCardsIntoHand(draw){

  let bonusDrawBuff = player.buffs.draw ?? 0
  let playerDraw = 5
    let totalDraw = draw ?? (playerDraw + bonusDrawBuff)

  for(let i = 1 ; i <= totalDraw ; i++ ){

    if(player.drawPile.length == 0){
      if (player.discardPile.length == 0) {break}
       cardfunctions.shuffleDrawPile()
    }

    let drawnCard = player.drawPile.pop()
    player.hand.push(drawnCard)

  }
  // console.log(player.hand);
  render() 
}

export function endTurnClearPlayerHand() {
  const playerHand =  Array.from(player.hand)
    for (let i = 0; i < playerHand.length; i++) {
      const card = playerHand[i];
      if (card.retains) {continue}
      else if (card.exhausts) {exhaustCard(card) ; continue}
      else{discardCard(card)}
    }
  render()
}


export function discardCard(card) {
//only from hand
const index = player.hand.findIndex(x => x === card)
const discardedCard = player.hand.splice(index,1)
  player.discardPile.push(...discardedCard)
  render()
}

export function exhaustCard(card) {
  const {index , exhaustedCard , location } = cardfunctions.locateCard(card)
  player[location].splice(index , 1)
  player.exhaustPile.push(exhaustedCard)
  render()
}


