  grid-auto-rows: 25px;
  gap: 5px;
  z-index: -1;
}

.healthBar {
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  margin: auto;
  color: white;
  border: 2px solid black;
  text-align: center;
  font-family: "Akshar", sans-serif;
  min-width: fit-content;
  max-width: 100%;
  height: 20px;
  transform-origin: center;
  transform: scale(1);
  opacity: 0.85;
  isolation: isolate;
  transition: width 500ms ease-out;
}
.healthBar .healthDisplay {
  position: absolute;
  height: 100%;
  width: 100%;
  background-color: red;
  z-index: -2;
  transition: transform 500ms ease-out;
  transform-origin: left;
}
.healthBar .healthDrag {
  position: absolute;
  height: 100%;
  width: 100%;
  background-color: rgb(255, 217, 0);
  z-index: -3;
  transition: transform 50ms ease-in;
  transform-origin: left;
}
.healthBar .blockBar {
  display: none;
}
.healthBar .blockBar.show {
  display: block;
  position: absolute;
  height: 100%;
  width: 100%;
  z-index: -1;
  outline: 3px solid lightgray;
}
