  position: absolute;
  right: 100%;
  bottom: 0;
  top: 0;
  margin: auto;
  height: 50px;
  width: 50px;
  background-image: url("/768b1088ee5c9e3d26067daec24caed3.png");
  background-size: contain;
  font-size: 20px;
  color: white;
  text-shadow: 2px 0 black, -2px 0 black, 0 2px black, 0 -2px black;
  display: grid;
  place-items: center;
  transform: translateX(25%);
}

.enemySelector, .friendlySelector {
  position: absolute;
  inset: 0;
  margin: auto;
  width: 100%;
  height: 100%;
  border: 5px dotted rgb(255, 217, 0);
  transition: transform 300ms ease-out;
  transform: scale(0);
}

.enemySelector.show, .friendlySelector.show {
  transform: scale(1);
}

.enemy-grid {
  position: absolute;
  right: 0;
  top: 20px;
  width: 70%;
  height: 60%;
  border: 3px solid red;
  transform-origin: bottom right;
  display: grid;
  grid-template-columns: repeat(4, 1fr);
  grid-template-rows: repeat(2, 1fr);
  isolation: isolate;
}

.enemy-box {
  position: relative;
  width: 100%;
  height: 100%;
  background-color: rgba(255, 255, 255, 0.363);
  border: 3px solid black;
  display: flex;
  isolation: isolate;
}

