@media (max-width: 1000px) {
  .enemy-grid {
    transform: scale(0.85);
  }
  .healthBar {
    transform: scale(0.85);
  }
  .actionIcon {
    font-size: unset;
    right: 0;
    left: 0;
    bottom: calc(100% + 10px);
    border-width: 4px;
    width: 40px;
    height: 40px;
    transform: scale(1);
  }
  .entityScan {
    transform-origin: top left;
    transform: scale(0.6);
  }
}
@media (max-width: 500px) {
  .actionIcon {
    right: 0;
    left: 0;
    bottom: calc(100% + 10px);
    border-width: 2px;
    width: 25px;
    height: 25px;
  }
  .entityScan {
    transform-origin: center;
    transform: scale(0.6) rotate(90deg);
    animation: scanInit2 200ms;
  }
  @keyframes scanInit2 {
    from {
      transform: scale(0) rotate(90deg);
    }
    to {
      transform: scale(0.6) rotate(90deg);
    }
  }
