  box-sizing: border-box;
}

.rest-of-page {
  position: absolute;
  top: 0;
  right: 0;
  width: 100vw;
  height: 100vh;
  background-color: rgb(0, 10, 66);
  overflow: hidden;
  transform-origin: top right;
  transition: all 250ms, transform 500ms ease-in;
}

@media (max-width: 500px) {
  .rest-of-page {
    width: 100vh;
    height: 100vw;
    transform: rotate(90deg) translateX(100%);
  }
}
.player {
  position: absolute;
  left: 10%;
  bottom: 40%;
  height: 200px;
  width: 145px;
  background-color: rgb(162, 162, 231);
}

@media (max-width: 500px) {
  .player {
    height: 150px;
    width: 100px;
  }
}
@media (max-width: 1000px) {
  .player {
    height: 150px;
    width: 100px;
  }
}
.hand {
  position: absolute;
  bottom: 0;
  left: 0;
  right: 0;
  margin: 0 auto;
  border: 3px dotted rgb(80, 77, 77);
  height: 30vh;
  width: 75%;
  isolation: isolate;
  display: flex;
  justify-content: space-around;
}
.hand::before {
  position: absolute;
  content: "";
  bottom: 0;
  left: 0;
  right: 0;
  height: 50px;
  width: 100%;
  background-color: rgb(80, 77, 77);
  z-index: -1;
}

.card {
  flex: 0 0 auto;
  background-color: #fff;
  border: 3px solid black;
  width: 150px;
  height: 200px;
  margin-left: -100px;
  transition: transform 150ms ease-out;
  transform-origin: center;
  z-index: 0;
}
.card:hover, .card:focus-within {
  transform: translateY(-30%) scale(1.2);
  z-index: 2;
}
.card:nth-of-type(1) {
  margin-left: 0;
}

.card.selectedCard {
  box-shadow: 0 0 10px 10px rgb(126, 213, 241);
  z-index: 1;
  transform: translateY(-30%) scale(1.2);
}

.Strike {
  background-color: red;
}

.Defend {
  background-color: rgb(52, 183, 226);
}

.Bash {
  background-color: orange;
}

.Poison {
  background-color: green;
}

.Bleed {
  background-color: crimson;
}

@media (max-width: 500px) {
  .hand {
    height: 30vw;
  }
}
@media (max-width: 1000px) {
  .card {
    transform: scale(0.7);
  }
  .card:hover, .card:focus {
    transform: translateY(-30%) scale(0.7);
    z-index: 2;
  }
  .card.selectedCard {
    box-shadow: 0 0 10px 10px rgb(126, 213, 241);
    z-index: 1;
    transform: translateY(-30%) scale(0.7);
  }
}
.enemy {
  position: absolute;
  inset: 0;
  margin: auto;
  width: 100%;
  isolation: isolate;
  background-size: contain;
  background-repeat: no-repeat;
  background-position: center;
  transform-origin: bottom left;
}
.enemy img {
  display: initial;
  position: absolute;
  width: 100%;
  height: auto;
  inset: 0;
  margin: auto;
  object-fit: contain;
}
.enemy .oneByTwo {
  height: 200%;
  transform: translateY(-25%);
}
.enemy .twoByTwo {
  transform: scale(2);
}

.tiger {
  width: 95%;
  background-image: url("/f779103044b4de14176e63c300373913.jpg");
}

.bat {
  background-image: url("/ecf72f906f8f8e100f9272747b99f2c9.gif");
}

.bruv {
  background-image: url("/63834a5f95cf87ce41880b3661999fc7.gif");
}

.attacking {
  animation-name: attacking;
  animation-duration: 300ms;
}

@keyframes attacking {
  50% {
    transform: translateX(-100%);
  }
  100% {
    transform: translateX(0%);
  }
}
.actionIcon {
  display: grid;
  place-content: center;
  color: white;
  box-sizing: initial;
  position: absolute;
  margin: auto;
  right: 0;
  bottom: 10px;
  width: 50px;
  height: 50px;
  font-size: 1.4em;
  text-shadow: 2px 0 black, -2px 0 black, 0 2px black, 0 -2px black;
}
.actionIcon:empty {
  display: none;
}

.healthBar {
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  margin: auto;
  color: white;
  border: 2px solid black;
  text-align: center;
  font-family: "Akshar", sans-serif;
  min-width: 30%;
  max-width: 100%;
  height: 20px;
