.healthBar .blockBar {
  display: none;
}
.healthBar .blockBar.show {
  display: block;
  position: absolute;
  height: 100%;
  width: 100%;
  z-index: -1;
  outline: 3px solid lightgray;
}
.healthBar .blockBar.show .blockCount {
  position: absolute;
  right: 100%;
  bottom: 0;
  top: 0;
  margin: auto;
  height: 50px;
  width: 50px;
  background-image: url("/768b1088ee5c9e3d26067daec24caed3.png");
  background-size: contain;
  font-size: 20px;
  color: white;
  text-shadow: 2px 0 black, -2px 0 black, 0 2px black, 0 -2px black;
