* {
  box-sizing: border-box;
}

.rest-of-page {
  position: absolute;
  top: 0;
  right: 0;
  width: 100vw;
  height: 100vh;
  background-color: rgb(0, 10, 66);
  overflow: hidden;
  transform-origin: top right;
  transition: all 250ms, transform 500ms ease-in;
}

@media (max-width: 500px) {
  .rest-of-page {
    width: 100vh;
    height: 100vw;
    transform: rotate(90deg) translateX(100%);
  }
}
.player {
  position: absolute;
  left: 10%;
  bottom: 40%;
  height: 200px;
  width: 145px;
  background-color: rgb(162, 162, 231);
}

@media (max-width: 500px) {
  .player {
    height: 150px;
    width: 100px;
  }
}
@media (max-width: 1000px) {
  .player {
    height: 150px;
    width: 100px;
  }
}
.hand {
  position: absolute;
  bottom: 0;
  left: 0;
  right: 0;
  margin: 0 auto;
  border: 3px dotted rgb(80, 77, 77);
  height: 30vh;
  width: 75%;
  isolation: isolate;
  display: flex;
  justify-content: space-around;
}
.hand::before {
  position: absolute;
  content: "";
  bottom: 0;
  left: 0;
  right: 0;
  height: 50px;
  width: 100%;
  background-color: rgb(80, 77, 77);
  z-index: -1;
}

.card {
  flex: 0 0 auto;
  background-color: #fff;
  border: 3px solid black;
  width: 150px;
  height: 200px;
  margin-left: -100px;
  transition: transform 150ms ease-out;
  transform-origin: center;
  z-index: 0;
}
.card:hover, .card:focus-within {
  transform: translateY(-30%) scale(1.2);
  z-index: 2;
}
.card:nth-of-type(1) {
  margin-left: 0;
}

.card.selectedCard {
  box-shadow: 0 0 10px 10px rgb(126, 213, 241);
  z-index: 1;
  transform: translateY(-30%) scale(1.2);
}

.Strike {
  background-color: red;
}

.Defend {
  background-color: rgb(52, 183, 226);
}

.Bash {
  background-color: orange;
}

@media (max-width: 500px) {
  .hand {
    height: 30vw;
  }
}
@media (max-width: 1000px) {
  .card {
    transform: scale(0.7);
  }
  .card:hover, .card:focus {
    transform: translateY(-30%) scale(0.7);
    z-index: 2;
  }
  .card.selectedCard {
    box-shadow: 0 0 10px 10px rgb(126, 213, 241);
    z-index: 1;
    transform: translateY(-30%) scale(0.7);
  }
}
