import * as dom from "../dom.js"
import {handElement,restOfPage,enemyGrid,enemyBoxes ,playerEnergyElement ,playerMaxEnergyElement , playerModel} from "../dom.js"
import { player } from "../player.js";
import pubsub from "../pubsub.js";
export let selectedCard ;

(function(){
  restOfPage.addEventListener("pointerdown" , (e)=>{
    deSelectHandler(e)
  })




})()

// small renders


export function generateCardDomElement(card) {

  let cardElement = document.createElement("div")

  let cardType = determineCardType(card)
  let cardName = card.name.replaceAll(" " , "")

  cardElement.classList.add("card" ,  cardType , cardName)
  cardElement.setAttribute("tabindex" , "0")

    const cardIcon = createCardIcon()
    cardElement.append(cardIcon)

    const nameHolder = document.querySelector(".nameHolder").cloneNode(true)
    nameHolder.classList.add("nameHolder")
    cardElement.append(nameHolder)

    const energyElement =  createEnergyElement(card)
    cardElement.append(energyElement)

    const nameElement = createNameElement(card)
    cardElement.append(nameElement)

    const cardDescription = createCardDescription(card)
    cardElement.append((cardDescription ?? "description"))

  card.element = cardElement
  cardElement.cardObject = card

  selectCardOnClick(cardElement)
}

    export function createEnergyElement(card){
      const card_energy = card.energyCost
      const card_energy_element = document.createElement("p")
      card_energy_element.classList.add("energyElement")
      card_energy_element.append(card_energy)
      return card_energy_element
    }

    export function createNameElement(card) {
      const name_element = document.createElement("p")
      name_element.classList.add("nameElement")
      name_element.append(card.name)
      return name_element
    }

    export function createCardIcon(){
      const card_icon = document.createElement("div")
      card_icon.classList.add(`cardIcon`)
      return card_icon
    }

    export function createCardDescription(card){
      const description_box = document.createElement("div")
      description_box.classList.add("descriptionBox")
      const card_discription = document.querySelector(`.description.${card.name.toLowerCase().replaceAll(" " , "")}`)?.cloneNode(true)
      description_box.append(card_discription)
      return description_box

    }
export function renderCardIntoHand(card){
  const cardParent = document.createElement("div")
  cardParent.className = "cardParent"
  let cardElement = card.element
  cardParent.append(cardElement)
  handElement.append(cardParent)
}



    export function visualyRenderCard(card) {
      if(card.element == undefined){
        generateCardDomElement(card)
      }
      renderCardIntoHand(card)
    }

    export function visualyRemoveCard(card) {
      const cardParent = card.element.parentElement
      card.element.remove()
      cardParent.addEventListener("animationend" , removeelement)
      cardParent.classList.add("removing")
    }
    function removeelement(e) {
      e.target.remove()
    }




    export function renderHealthBar(entity) {
    const healthInfo = getEntityHealthModals(entity)
    const healthBar = healthInfo.healthBar
      healthBar.style.width = (entity.maxHealth + 40) + "px"
    }

    export function renderHealthCount(entity){
      const healthInfo = getEntityHealthModals(entity)
        const healthCount = healthInfo.healthCount
        const maxHealthCount = healthInfo.maxHealthCount

        maxHealthCount.innerText = entity.maxHealth
        healthCount.innerText = (entity.health < 0)? 0 : entity.health
    }

    export function renderHealthD(healthD , percent) {
      healthD.style.transform = `scaleX( max(0 ,${percent}))`
    }

    export function renderBlock(entity) {
      const healthInfo = getEntityHealthModals(entity)
        const blockBar = healthInfo.blockBar
        const blockCount = healthInfo.blockCount

      if (entity.block <= 0) {
        blockBar.classList.toggle("show" , false)
      }

      else {
        blockBar.classList.toggle("show" , true)
        blockCount.innerText = entity.block
      }

    }

    export function renderPlayerEnergy(){
      playerEnergyElement.innerText = player.energy
      playerMaxEnergyElement.innerText = player.maxEnergy
    }


export function renderEntityAttackedAnimation(entity , damageObj) {
  let enemyBox ;
if (entity === player) { enemyBox = entity.modal }
else{enemyBox = entity.modal.parentElement}

  let animation = damageObj.animation ??  dom.basic_slash
      animation = animation.cloneNode(true)
      animation.classList.remove("hidden")
  const animation_duration = animation.getAttribute("data-duration")

  enemyBox.append(animation)
  setTimeout(() => {
    animation.remove()
  }, animation_duration);
}

export function generateEffectEllement(entity ,effect ) {
  const effect_element_exists = entity.modal.querySelector(`.effectIcon.${effect.name}`)
if (effect_element_exists) { return }

  const effectElement = document.createElement("div")
  effectElement.classList.add("effectIcon" , effect.name)
    const usesElement = document.createElement("div")
    usesElement.classList.add("effectUses")
    const durationElement = document.createElement("div")
    durationElement.classList.add("effectDuration")

    effectElement.append(durationElement , usesElement)
    entity.effectGrid.append(effectElement)
}

export function generateEntityScan(entity) {
  const entityScan = dom.entityScan.cloneNode(true)

    for(const effect in entity.buffs){
      const effectElement = document.querySelector(`.effect_description.${effect.name}`).cloneNode(true)
      if (effectElement) {entityScan.append(effectElement)}
    }
    for(const effect in entity.debuffs){
      const effectElement = document.querySelector(`.effect_description.${effect}`).cloneNode(true)
      if (effectElement) {entityScan.querySelector(".scannedDebuffs").append(effectElement)}
    }

  return entityScan
}
export function removeAllEntityScans() {
  const all_scans = document.querySelectorAll(".entityScan")
  all_scans.forEach(scan => { 
    scan.remove()})
}

// /small renders



// checks
export function checkElement(thiselement , wantedElement) {
  return thiselement === wantedElement
}

export function checkIfIncludesClass(element , classToChack ){
  return Array.from(element.classList).includes(classToChack)
}
// /checks




// selection

export function deSelectHandler(e) {
  const is_restOfPage = checkElement(e.target , restOfPage)

  const is_enemyBox = checkIfIncludesClass(e.target , "enemy-box") ??
                     !checkIfIncludesClass(e.target.firstElementChild , "entity")
  const handElement = Array.from(e.target.classList).includes("")
  if(is_restOfPage || is_enemyBox ) {deSelectAllCards()}
}


export function selectCardOnClick(cardElement){

  const cardObject = cardElement.cardObject

  cardElement.addEventListener("pointerdown" , (e)=> {
    selectCard(cardElement)
  })
}



export function selectCard(cardElement){
  deSelectAllCards()
  cardElement.classList.add("selectedCard")      
  selectedCard = cardElement
}



export function deSelectAllCards() {
  let allCards = document.querySelectorAll(".card")
  allCards.forEach(card => {
    card.classList.remove("selectedCard")
  })
  selectedCard = null
}


export function deSelectAllEntities() {
  selectAllEnemies(false)
  selectAllFriendlies(false)
  selectPlayer(false)
}

    export function selectAllEnemies(status=true) {

      let selectors = enemyGrid.querySelectorAll(".enemySelector")
      selectors.forEach(selector => {
        selector.classList.toggle("show" , status)
      })
    } 

    export function selectAllFriendlies(status=true) {

      let selectors = document.querySelectorAll(".friendlySelector")
      selectors.forEach(selector => {
        selector.classList.toggle("show" , status)
      })
    }

    export function selectPlayer(status=true) {

      let playerModal = document.querySelector(".player")
      let playerSelector = playerModal.querySelector(".friendlySelector")

      playerSelector.classList.toggle("show" , status)
    }
// /selection

// misc
export function linkElementtToEntity(Element , entity) {
  Element.entityObject = entity
}

export function determineCardType(card) {
  return card.constructor.generatedCardType
}

export function getEntityHealthModals(entity){
  const healthBar = entity.healthBar
  const maxHealthCount = healthBar.querySelector(".maxHealthCount")
  const healthCount = healthBar.querySelector(".healthCount")
  const healthDisplay = healthBar.querySelector(".healthDisplay")
  const healthDrag = healthBar.querySelector(".healthDrag")
  const blockBar = healthBar.querySelector(".blockBar")
  const blockCount = blockBar.querySelector(".blockCount")

  return { healthBar ,maxHealthCount , healthCount , healthDisplay , healthDrag , blockBar , blockCount}
}
export function moveNextEnemyBoxOf(enemyBox) {
  const enemyBoxPosition = findEnemyBoxPosition(enemyBox)
  if(!enemyBoxes[(enemyBoxPosition + 1)]){ pubsub.turnStarted() ; return}
    enemyBoxes[(enemyBoxPosition + 1)].move()

}
export function findEnemyBoxPosition(enemyBox) {
  return enemyBoxes.findIndex( (eBox)=> eBox === enemyBox )
}

export function getEnemyBoxOfEntity(entity) {
  return entity.modal.parentElement
}

export function findNextFriendly(enemy) {


  const enemyBox = getEnemyBoxOfEntity(enemy)
  const enemyBoxIndex = findEnemyBoxPosition(enemyBox)
    for (let i = enemyBoxIndex + 1; i < enemyBoxes.length; i++) {
      const Ebox = enemyBoxes[i];
      const friendlyModal = enemyBoxes[i].querySelector(".friendly")
      if (friendlyModal) {
        return friendlyModal.entity
      }
    }

  return player
}


export function positionEntityScan(entityScan , entity) {
  const enemy_box_position = entity.modal.getBoundingClientRect()
  const restOfPage_position = restOfPage.getBoundingClientRect()
      const screenWidth = (restOfPage_position.width > restOfPage_position.height)? restOfPage_position.width:restOfPage_position.height; 

  const ebox_in_left = enemy_box_position.left < (screenWidth / 2)
  let X_bonus ;
  if(ebox_in_left ){
      X_bonus = 1
     }
  else{X_bonus = -1}

  entityScan.style.top = enemy_box_position.top + "px"
  entityScan.style.left = (enemy_box_position.left + (X_bonus * enemy_box_position.width)) + "px"

}
// / misc
