import effects from "../buffsManager"
import { renderEffect } from "../functions"
import pubsub from "../pubsub.js"

export const renderEffect2 = renderEffect
export {renderEffect} from "../functions"
// add effect
// stacking
  export function applyEffectHandler(effect , entity) {
    const type_of_effect = effect.constructor.generatedEffect
    const effect_list = entity[type_of_effect]
      const effectName = effect.name
      const effectDuration = effect.duration
      const effectUses = effect.uses

  // link 'em
      effect.hostingEntity = entity

    if(effect_list[effectName]){
  // reinforce effect if exists
      reinforceEffect( effect_list[effectName] , effectDuration , effectUses)
      effect.alreadyAplied = true
    }

    else{
  // add effect if it doesn't exist
      effect_list[effectName] = effect
    }

      renderEffect(entity , effect_list[effectName])
    }

  export function applyStackingEffectHandler(effect , entity){
    const type_of_effect = effect.constructor.generatedEffect
    const effect_list = entity[type_of_effect]
      const effectName = effect.name

    if(effect_list[effectName]){
      for(let i = 0 ; i < 99 ; i++){
        if(effect_list[effectName + i] != undefined){continue;}
          effect.name = effectName + i
          break;
      }
    }
  // add effect with original / newname
    effect_list[effectName] = effect
    renderEffect(entity , effect)

  }

// non stacking


// adding effects to list
 // deal damae
  export function addTodealDamageEffects(effect) {
    effect.hostingEntity.dealDamageEffects[effect.name] = effect
  }
  export function removeFromdealDamageEffects(effect) {
    delete effect.hostingEntity.dealDamageEffects[effect.name]
  }

// recieve damage
  export function addTorecieveDamageEffects(effect) {
    effect.hostingEntity.recieveDamageEffects[effect.name] = effect
  }
  export function removeFromrecieveDamageEffects(effect) {
    delete effect.hostingEntity.recieveDamageEffects[effect.name]
  }

// block effects
  export function addToblockEffects(effect) {
    effect.hostingEntity.blockEffects[effect.name] = effect
  }
  export function removeFromblockEffects(effect) {
    delete effect.hostingEntity.blockEffects[effect.name]
  }

// card effects
  export function addTocardEffects(effect , numOdList) {
  const cardList = getListBasedOnNumber(numOdList)
    effect.hostingEntity.cardEffects[cardList][effect.name] = effect
  }
  export function removeFromcardEffects(effect , numOdList) {
  const cardList = getListBasedOnNumber(numOdList)
    delete effect.hostingEntity.cardEffects[cardList][effect.name]
  }
    function getListBasedOnNumber(number) {
        if(number === 1){return "playEffects"}
        if(number === 2){return "drawEffects"}
        if(number === 3){return "exaustEffects"}
        if(number === 4){return "discardEffects"}
    }

// turn effects
  export function addToturnEffects(effect) {
    effect.hostingEntity.turnEffects[effect.name] = effect
  }
  export function removeFromturnEffects(effect) {
    delete effect.hostingEntity.turnEffects[effect.name]
  }

// /add effect


// effect timer
  export function halfEffectDuration(effect) {
    if(typeof(effect.duration) !== "number"){ throw new Error("inf effect want to be hallfed" , effect) ; return}
    effect.duration = Math.floor(effect.duration / 2)
    if(effect.duration <= 0){effect.remove()}
    renderEffect(effect.hostingEntity , effect)
  }

  export function reduceEffectDuration(effect) {
    if(typeof(effect.duration) !== "number"){return}
    effect.duration--
    if(effect.duration <= 0){effect.remove()}
    renderEffect(effect.hostingEntity , effect)

  }

  export function reduceEffectUses(effect) {
    if(typeof(effect.uses) == "string"){return}
    effect.uses--
    if(effect.uses <= 0){effect.remove()}
    renderEffect(effect.hostingEntity , effect)

  }
// /effect timer

// reinforce effect
function reinforceEffect(exactEffect , bonusDuration , bonusUses) {

  reinforceDuration(exactEffect , bonusDuration)
  reinforceUses(exactEffect , bonusUses)

}

      function reinforceDuration(exactEffect ,bonusDuration ) {
        if(exactEffect.duration === "inf"){return}

        else if(bonusDuration === "inf"){
          exactEffect.duration = "inf"
          return
        }

        else if(typeof(bonusDuration) === "number"){
          exactEffect.duration += bonusDuration
          return
        }
// fall back
        else{
          console.log( bonusDuration," added to " , exactEffect );  
          alert("effectsHandler : type of effect duration not intended , chack consle")
        }

      }


      function reinforceUses(exactEffect ,bonusUses ) {
        if(exactEffect.uses === "inf"){return}

        else if(bonusUses == "inf"){
          exactEffect.uses = "inf"
          return
        }

        else if(typeof(bonusUses) == "number"){
          exactEffect.uses += bonusUses
          return
        }
// fall back
        else{alert("effectsHandler : type of effect duration not intended , chack consle")
            console.log( bonusUses," added to " , exactEffect );  
        }
      }
// /reinforce effect

