import {attackFriendly,gainBlock,resetEntity,killEntityModal , renderEntityScan} from "../functions.js"
import * as entityMoves from "./entityMoves.js" 

export class basicEnemy{
  constructor(name,health , initBlock=0){
  this.name = name
    this.maxHealth = health
    this.health = health
    this.block = initBlock 

    this.turn = 0

    this.buffs = {}
    this.debuffs = {}

        this.str = 0
        this.dex = 0
        this.atkMod = 1
        this.defMod = 1

    this.dealDamageEffects = {}
    this.recieveDamageEffects = {}
    this.gainBlockEffects = {}
    this.clearBlockEffects = {}
    this.turnEffects = {}

    this.delay = 500
    this.deathDelay = 700
    generateEnemyElement(this)
  }

  die(entity){
  if(this.dead){return}
    resetEntity(this)
    killEntityModal(this)
    this.dead = true
    delete this
  }

  move(){
    this.moves[this.turn].exe()
    this.turn++
    if (this.turn >= this.moves.length) {this.turn = 0}
  }
  apply(effectObj){
    effectObj.apply(this)
  } 
  remove(effectName){
    this.buffs[effectName]?.remove()
    this.debuffs[effectName]?.remove()
  }
}

export class bat extends basicEnemy{
  constructor(){
    super("bat", 11 )
    const enemy = this

    this.moves = [
      new entityMoves.basic_deffend(enemy , 12),
      new entityMoves.basic_attack_friendly(enemy , 5) ,
    ]

  }
}

export class tiger extends basicEnemy{
  constructor(){
    super("tiger" , 63)
    const enemy = this
    this.moves = [
      new entityMoves.basic_attack_friendly(enemy , 50) ,
      new entityMoves.basic_attack_friendly(enemy , 20) ,
    ]
  }
}

export class bruv extends basicEnemy{
  constructor(){
    super("bruv" , 36)
    const enemy = this

    this.moves = [
      {exe(){} , icon:"" , render(){}},

    ]
  }
}

export class g extends basicEnemy{
  constructor(){
    super("" , 2)
    const enemy = this

    this.moves = [
      {exe(){} , icon:""},
    ]
  }
}

