import {player} from "../player.js"

export function resetDrawPile() {
  let shuffled_deck = shuffle(player.deck)

  player.discardPile = [] ; player.playerHand = []
  player.drawPile = shuffled_deck

} 

    export function shuffle(array) {
      let our_array = Array.from(array)
      let shuffled_array = []

      for(let i = 0 ; i < array.length ; i++){
        let random_index = Math.floor(Math.random() * our_array.length )
        shuffled_array.push(...our_array.splice(random_index , 1))
      }

      return shuffled_array
    } 

    export function determineCardType(card) {
      return card.constructor.generatedCardType
    }

export function shuffleDrawPile(){
  // asuming drawPile has 0 cards

  let shuffled_discard = shuffle(player.discardPile)
  clearDiscardPile()
  player.drawPile = [...shuffled_discard]
}
function clearDiscardPile() {
  player.discardPile = []
}
      /* returns object */
export function locateCard(card) {

  let index = player.hand.findIndex(x => x === card)
  let locatedCard ; let location ;
  if (index !== -1) { 
     locatedCard = player.hand[index]
     location = "hand"
  }
  if (index === -1) {
    index = player.drawPile.findIndex(x => x === card)
     locatedCard = player.drawPile[index]
     location = "drawPile"

  }
  if (index === -1) {
    index = player.discardPile.findIndex(x => x === card)
     locatedCard = player.discardPile[index]
     location = "discardPile"
  } 
  if (index === -1) {
    index = player.exhaustPile.findIndex(x => x === card)
     locatedCard = player.exhaustPile[index]
     location = "exhaustPile"
  } 
return {index , locatedCard , location}
}
